<script>import { range, durationUnitRegex } from './utils';
export let color = '#FF3E00';
export let unit = 'px';
export let duration = '2.1s';
export let size = '60';
export let pause = false;
let durationUnit = duration.match(durationUnitRegex)?.[0] ?? 's';
let durationNum = duration.replace(durationUnitRegex, '');
</script>

<div class="wrapper" style="--size: {size}{unit}; --color: {color}">
	{#each range(2, 1) as version}
		<div
			class="circle"
			class:pause-animation={pause}
			style="animation: {duration} {version === 1
				? `${(+durationNum - 0.1) / 2}${durationUnit}`
				: `0s`} infinite ease-in-out"
		/>
	{/each}
</div>

<style>
	.wrapper {
		position: relative;
		width: var(--size);
		height: var(--size);
	}
	.circle {
		position: absolute;
		width: var(--size);
		height: var(--size);
		background-color: var(--color);
		border-radius: 100%;
		opacity: 0.6;
		top: 0;
		left: 0;
		animation-fill-mode: both;
		animation-name: bounce !important;
	}
	.pause-animation {
		animation-play-state: paused;
	}
	@keyframes bounce {
		0%,
		100% {
			transform: scale(0);
		}
		50% {
			transform: scale(1);
		}
	}
</style>
